import React,{useState} from 'react';
import './App.css';
import SelectPer from './Component/selectPersonalizado'

function App() {
  // eslint-disable-next-line react-hooks/rules-of-hooks
  const [valorSelecionado, setValorSelecionado] = useState([]);

  const listaDeFrutas = ['Maçãs','Uvas','Bananas','Abacaxi','Morangos'];

  const OptionOuter =     [
      "CLT", "Sócio", "Diretor Estatutário", "Estágio", "Aprendiz", "Trabalhador Autônomo", "Trabalhador Voluntário", "Trabalhador Temporário", "Trabalhador Rural", "Pessoa Jurídica", "Contrato Intermitente", "Contrato por Tempo Determinado", "Teletrabalho"
  ]

  const myChangeHandlerUm = event => {

    let nam = event.target.name;
    let val = event.target.value;
    console.log("Um", nam, val)

    setValorSelecionado({ ...valorSelecionado, [nam]: val });
  }

  const myChangeHandlerDois = event => {

    let nam = event.target.name;
    let val = event.target.value;
    console.log("Dois", nam, val)
  }
  return (
    <div className="App">
     <h1>Começo de tudo</h1>
     <SelectPer option={listaDeFrutas} valorCampo={valorSelecionado.val} nameCampo="Frutas" myChangeHandler={myChangeHandlerUm} />
     <br />
     <br />
     <br />
     <br />
     <br />
     <br />
     <br />
     <br />
     <br />
     <SelectPer option={OptionOuter}nameCampo="nomeSector" myChangeHandler={myChangeHandlerDois}/>
    </div>
  );
}

export default App;
